import React, { createContext, useContext, useState, useEffect } from "react";
import toast from "react-hot-toast";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem("adminToken"));

  // Check auth status on mount
  useEffect(() => {
    if (token) {
      checkAuthStatus();
    } else {
      setLoading(false);
    }
  }, [token]);

  const checkAuthStatus = async () => {
    try {
      // For demo purposes, check if we have a demo token
      if (token && token.startsWith("demo-token-")) {
        // Create demo user data
        const demoUser = {
          id: 1,
          name: "Admin User",
          email: "admin@example.com",
          role: "admin",
          avatar: "https://via.placeholder.com/40x40/3B82F6/FFFFFF?text=AU",
        };
        setUser(demoUser);
      } else {
        // If no valid demo token, logout
        logout();
      }
    } catch (error) {
      console.error("Auth check failed:", error);
      logout();
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      // Demo authentication - no backend required
      if (email === "admin@example.com" && password === "admin123") {
        const demoUser = {
          id: 1,
          name: "Admin User",
          email: "admin@example.com",
          role: "admin",
          avatar: "https://via.placeholder.com/40x40/3B82F6/FFFFFF?text=AU",
        };

        const demoToken = "demo-token-" + Date.now();

        setToken(demoToken);
        setUser(demoUser);
        localStorage.setItem("adminToken", demoToken);

        toast.success("Login successful!");
        return { success: true };
      } else {
        toast.error(
          "Invalid credentials. Use demo credentials: admin@example.com / admin123"
        );
        return { success: false, message: "Invalid credentials" };
      }
    } catch (error) {
      const message = error.response?.data?.message || "Login failed";
      toast.error(message);
      return { success: false, message };
    }
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem("adminToken");
    toast.success("Logged out successfully");
  };

  const updateProfile = async (profileData) => {
    try {
      // Demo mode - just update local user state
      if (user) {
        const updatedUser = { ...user, ...profileData };
        setUser(updatedUser);
        toast.success("Profile updated successfully! (Demo mode)");
        return { success: true };
      } else {
        toast.error("No user logged in");
        return { success: false, message: "No user logged in" };
      }
    } catch (error) {
      toast.error("Profile update failed");
      return { success: false, message: "Profile update failed" };
    }
  };

  const changePassword = async (currentPassword, newPassword) => {
    try {
      // Demo mode - just show success message
      if (currentPassword === "admin123") {
        toast.success("Password changed successfully! (Demo mode)");
        return { success: true };
      } else {
        toast.error("Current password is incorrect");
        return { success: false, message: "Current password is incorrect" };
      }
    } catch (error) {
      toast.error("Password change failed");
      return { success: false, message: "Password change failed" };
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    updateProfile,
    changePassword,
    isAuthenticated: !!user,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
